/* MQTT Configuration */

var mqtt = require("mqtt");

var Broker_URL = "mqtt://127.0.0.1";
var options = {
  clientId: "LEDMQTT",
  port: 1883,
  keepalive: 60,
};

/* Section End */


function LEDButton() {
  // Declare a new state variable, which we'll call "count"  const [count, setCount] = useState(0);




    // if(count%2 === 0 ){
