import React, { useEffect, useState, createRef } from "react";
import classNames from "classnames";
import { useForm } from "react-hook-form";
import DatePicker from "react-datepicker";
import { CRow, CCol, CCard, CCardHeader, CCardBody } from "@coreui/react";
import { rgbToHex } from "@coreui/utils";
import { DocsLink } from "src/reusable";
import axios from "axios";
import Plot from "react-plotly.js";
import "react-datepicker/dist/react-datepicker.css";

function DbForm1() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const [startDate, setStartDate] = useState(new Date());

  //Mon May 10 2021 12:00:03 GMT+0800 (Malaysia Time)           Change this to MM/dd/yyyy
  const convertDate = () => {
    let str = startDate.toString();
    let parts = str.split(" ");
    let months = {
      Jan: "01",
      Feb: "02",
      Mar: "03",
      Apr: "04",
      May: "05",
      Jun: "06",
      Jul: "07",
      Aug: "08",
      Sep: "09",
      Oct: "10",
      Nov: "11",
      Dec: "12",
    };
    return months[parts[1]] + "/" + parts[2] + "/" + parts[3];
  };

  const onSubmit = (data) => {
    console.log(data.Node);
    console.log(convertDate(startDate));

    console.log("The Submit button was clicked");
    axios.get(`http://127.0.0.1:9000/data?node=${data.Node}&date=${convertDate(startDate)}`).then((response)=>{
      console.log('response', response);
      //Data coming in from API, should be sent to create the chart.
    })
    .catch((error)=>{
    console.log(error);
    })
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <CRow>
        <CCol>
          <label>Node: </label>
          <select {...register("Node")}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="6">6</option>
          </select>
        </CCol>
        <CCol>
          <label>Select the Date</label>
          <DatePicker
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            dateFormat="MM/dd/yyyy"
          />
        </CCol>
        <CCol>
          <input type="submit" />
        </CCol>
      </CRow>
    </form>
  );
}

export default class DataReviewChart extends React.Component {
  state = {
    line1: {
      x: [0],
      y: [0],
      name: "Line 1",
    },
    // line2: {
    //   x: [1, 2, 3],
    //   y: [-3, -2, -1],
    //   name: 'Line 2',
    // },

    layout: {
      datarevision: 0,
    },
    revision: 0,
    startDate: Date()
  };

  componentDidMount() {
    // setInterval(this.increaseGraphic, 1000)
    // socket.on("ana", (data) => {
    //   this.increaseGraphic(data);
    // });
  }

  convertDate = () => {
    let str = this.state.startDate.toString();
    let parts = str.split(" ");
    let months = {
      Jan: "01",
      Feb: "02",
      Mar: "03",
      Apr: "04",
      May: "05",
      Jun: "06",
      Jul: "07",
      Aug: "08",
      Sep: "09",
      Oct: "10",
      Nov: "11",
      Dec: "12",
    };
    return months[parts[1]] + "/" + parts[2] + "/" + parts[3];
  };


   handleSubmit = (data) => {
    console.log(data.Node);
    console.log(this.convertDate(this.state.startDate));

    console.log("The Submit button was clicked");
    axios.get(`http://127.0.0.1:9000/data?node=${data.Node}&date=${this.convertDate(this.state.startDate)}`).then((response)=>{
      console.log('response', response);
      //Data coming in from API, should be sent to create the chart.
    })
    .catch((error)=>{
    console.log(error);
    })
  };

  increaseGraphic = ({ value, analogTwo, timeStamp }) => {
    const { line1, line2, layout, revision } = this.state;
    line1.x.push(revision);
    line1.y.push(value);



    this.setState({ revision: this.state.revision + 1 });
    layout.datarevision = this.state.revision + 1;
  };

  render() {
    return (
      <div>
        <CCard>
          <CCardHeader>Review Past Data</CCardHeader>
          <CCardBody>
            {/* <DbForm1 /> */}
            <form onSubmit={this.handleSubmit()}>
              <CRow>
                <CCol>
                  <label>Node: </label>
                  <select>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                    <option value="6">6</option>
                  </select>
                </CCol>
                <CCol>
                  <label>Select the Date</label>
                  <DatePicker
                    selected={this.state.startDate}
                    onChange={(date) => {this.setState({startDate: date})}}
                    dateFormat="MM/dd/yyyy"
                  />
                </CCol>
                <CCol>
                  <input type="submit" />
                </CCol>
              </CRow>
            </form>



          </CCardBody>

          <CCardHeader></CCardHeader>
          <CCardBody>
            <Plot
              data={[this.state.line1]}
              layout={this.state.layout}
              revision={this.state.revision}
              useResizeHandler={true}
              style={{ width: "100%", height: "100%" }}
              graphDiv="graph"
            />
          </CCardBody>
        </CCard>
      </div>
    );
  }
}
