import React, { useEffect, useState, createRef } from "react";
import classNames from "classnames";
import { useForm } from "react-hook-form";
import DatePicker from "react-datepicker";
import { CRow, CCol, CCard, CCardHeader, CCardBody } from "@coreui/react";
import { rgbToHex } from "@coreui/utils";
import { DocsLink } from "src/reusable";
import axios from "axios";
import Plot from "react-plotly.js";
import "react-datepicker/dist/react-datepicker.css";

const ReviewDataChart = () => {

  //This is where I collect data from the API response and put in an array.
  const [data, setData] = useState([]);


  function DbForm1() {
    const {
      register,
      handleSubmit,
      formState: { errors },
    } = useForm();



    const [startDate, setStartDate] = useState(new Date());

    //Mon May 10 2021 12:00:03 GMT+0800 (Malaysia Time)           Change this to MM/dd/yyyy
    const convertDate = () => {
      let str = startDate.toString();
      let parts = str.split(" ");
      let months = {
        Jan: "01",
        Feb: "02",
        Mar: "03",
        Apr: "04",
        May: "05",
        Jun: "06",
        Jul: "07",
        Aug: "08",
        Sep: "09",
        Oct: "10",
        Nov: "11",
        Dec: "12",
      };
      return months[parts[1]] + "/" + parts[2] + "/" + parts[3];
    };

    useEffect(()=>{
      const onSubmit = (data) => {
        console.log(typeof data.Node);
        console.log(convertDate(startDate));
        console.log("The Submit button was clicked");

        //API GET call to fetch old data.
        axios
          .get(
            `http://127.0.0.1:9000/data?node=${data.Node}&date=${convertDate(
              startDate
            )}`
          ) //Makes the GET request from the backend.
          .then((response) => {
            console.log(response);

            let timeArr = [];
            let dataArr = [];
            setData(()=>{
              data.timeArr = response.time;
              data.dataArr = response.data;
            })


            //Data coming in from API, should be sent to create the chart.
          })
          .catch((error) => {
            console.log(error);
          });
      };
    });


    return (
      <form onSubmit={handleSubmit(onSubmit)}>
        <CRow>
          <CCol>
            <label>Node: </label>
            <select {...register("Node")}>
              <option value="1">1</option>
              <option value="2">2</option>
            </select>
          </CCol>
          <CCol>
            <label>Select the Date</label>
            <DatePicker
              selected={startDate}
              onChange={(date) => setStartDate(date)}
              dateFormat="MM/dd/yyyy"
            />
          </CCol>
          <CCol>
            <input type="submit" />
          </CCol>
        </CRow>
      </form>
    );
  }

  return(
    <div>
    <CCard>
      <CCardHeader>Review Past Data</CCardHeader>
      <CCardBody>
        <DbForm1 />
      </CCardBody>

      <CCardHeader></CCardHeader>
      <CCardBody>
        <Plot
          data={[
            {
              x: timeArr,
              y: dataArr,
              type: "scatter",
              marker: { color: "red" },
            },
          ]}
          useResizeHandler={true}
          style={{ width: "100%", height: "100%" }}
          graphDiv="graph"
        />
      </CCardBody>
    </CCard>
  </div>
  );

};
