import React, { useEffect, useState } from 'react'
import { CWidgetDropdown, CRow, CCol, CDropdown, CDropdownMenu, CDropdownItem, CDropdownToggle } from '@coreui/react'
import CIcon from '@coreui/icons-react'
import ChartBarSimple from '../charts/ChartLineSimple'
import io from 'socket.io-client'

const socket = io('http://localhost:9000', {
    transports: ['websocket', 'polling'],
})

const LightOutsideWidget = () => {
    let [LightOutside, setLightOutside] = useState(0)

    useEffect(() => {
        socket.on('DataStream', (data) => {
            setLightOutside((LightOutside = data.LightOut))
        })
    })

    // render
    return (
        <>
            <CCol>
                <CWidgetDropdown
                    color="gradient-warning"
                    header={LightOutside}
                    text="Light Intensity Outside"
                    footerSlot={
                        <ChartBarSimple
                            className="mt-3 mx-3"
                            style={{ height: '70px' }}
                            backgroundColor="warning"
                            label="Members"
                            labels="months"
                        />
                    }
                ></CWidgetDropdown>
            </CCol>
        </>
    )
}

export default LightOutsideWidget
