import React, { Component } from "react";
import Switch from "react-switch";

/* MQTT Configuration */

var mqtt = require("mqtt");

var Broker_URL = "mqtt://127.0.0.1";
var options = {
  clientId: "LEDMQTT",
  port: 1883,
  keepalive: 60,
};



/* Section End */

class SwitchExample extends Component {
  constructor() {
    super();
    this.state = { checked: false };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(checked) {

    //is this where i will write code to send an mqtt command?
    var mqttclient = mqtt.connect(Broker_URL, options);
    mqttclient.on("connect", () => {
      //Callback Function connects to the broker.
      var Topic = "esp32/led";

      console.log("Connecting MQTT");
      mqttclient.subscribe(Topic, (err, granted) => {
        //This callback function subscribes to the preset topic.
        console.log("Subscribed to " + Topic);

        if(this.state.checked === true){
          mqttclient.publish(Topic, 'ON');
          console.log(`Publishing`+ this.state.checked);
        }else{
          mqttclient.publish(Topic, 'ON');
        }
      });
    });

    this.setState({ checked });
  }

  render() {
    return (
      <label>
        <Switch onChange={this.handleChange} checked={this.state.checked} />
      </label>
    );
  }
}

export default SwitchExample;
