import Plot from 'react-plotly.js';
import io from 'socket.io-client';


//======================Socket Setup==========================

const socket = io("http://localhost:3001", {
    transports: ['websocket', 'polling']
});

//=====================Section End============================



const SampleChart = ()=> {

    //This is where I collect data over socket and put in an array.
    const [data, setData] = useState([]);

    // 1. listen for a Analog event and update the state.
    useEffect(() => {

        socket.on('ana', Analog => {

            console.log('ANALOG',Analog)
            // setData(currentData => [...currentData, Analog]);
        });
    }, []);
        return(
            <Plot 
                data = {[
                    {
                        x: [1,2,3],
                        y: [2,6,2],
                        //type: 'scatter',
                        mode: 'lines',
                        marker: {color: 'red'},
                },
            ]}
            layout = {{width: 800, height: 400, title:'Sample Chart'}}
            />
        );
}

export default SampleChart;
