import React, { useEffect, useState, createRef } from "react";
import classNames from "classnames";
import { useForm } from "react-hook-form";
import DatePicker from "react-date-picker";
import { CRow, CCol, CCard, CCardHeader, CCardBody } from "@coreui/react";
import { rgbToHex } from "@coreui/utils";
import { DocsLink } from "src/reusable";
import axios from "axios";
import Plot from "react-plotly.js";

function DbForm1() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const [datevalue, onChange] = useState(new Date());



  const onSubmit = (data, datevalue) => {


    console.log(data.Node);
    console.log(data.datevalue);
    console.log('The Submit button was clicked');
    // axios.get(`localhost:9000/data?node=${data.Node}&date=${data.date}`)
    // .then((response)=>{
    //   //Data coming in from API, should be sent to create the chart.
    // })
    // .catch((error)=>{
    //   
    // })
  };




  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <CRow>
        <CCol>
          <label>Node: </label>
          <select {...register("Node")}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="6">6</option>
          </select>
        </CCol>
        <CCol>
          <label>React Datepicker</label>
          <DatePicker onChange={onChange} value={datevalue} />
        </CCol>
        <CCol>
          <input type="submit" />
        </CCol>          
      </CRow>
    </form>
  );
}

export default class DataReviewChart extends React.Component {
  state = {
    line1: {
      x: [0],
      y: [0],
      name: "Line 1",
    },
    // line2: {
    //   x: [1, 2, 3],
    //   y: [-3, -2, -1],
    //   name: 'Line 2',
    // },

    layout: {
      datarevision: 0,
    },
    revision: 0,
  };

  componentDidMount() {
    // setInterval(this.increaseGraphic, 1000)
    // socket.on("ana", (data) => {
    //   this.increaseGraphic(data);
    // });
  }

  increaseGraphic = ({ value, analogTwo, timeStamp }) => {
    const { line1, line2, layout, revision } = this.state;
    line1.x.push(revision);
    line1.y.push(value);

    // if (line1.x.length >= 10) {
    //   line1.x.shift()
    //   line1.y.shift()
    // }

    // line2.x.push(this.rand())
    // line2.y.push(this.rand())
    // if (line2.x.length >= 10) {
    //   line2.x.shift()
    //   line2.y.shift()
    // }

    this.setState({ revision: this.state.revision + 1 });
    layout.datarevision = this.state.revision + 1;
  };

  render() {
    return (
      <div>
        <CCard>
          <CCardHeader>Review Past Data</CCardHeader>
          <CCardBody>
            <DbForm1 />
          </CCardBody>

          <CCardHeader></CCardHeader>
          <CCardBody>
            <Plot
              data={[this.state.line1]}
              layout={this.state.layout}
              revision={this.state.revision}
              useResizeHandler={true}
              style={{ width: "100%", height: "100%" }}
              graphDiv="graph"
            />
          </CCardBody>
        </CCard>
      </div>
    );
  }
}
